import React, { Component } from 'react';
import './App.css';
import 'react-bootstrap-table/dist/react-bootstrap-table.min.css';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import {fetchAllLauches} from './modules/action.js';
import { connect } from "react-redux";


class App extends Component {
  constructor(props){
    super(props)
    props.dispatch(fetchAllLauches());
  }

  onSort(event, sortKey){
    const data = this.state.launches;
    data.sort((a, b) => a[sortKey].localeCompare(b[sortKey]))
    this.setState({launches})
  }

  componentDidMount() {
    fetchAllLauches();
  }

  render() {
    const { isLoading, launches, error } = this.props;

    return (
      <div className='App'>
        <h3>Frontend React Coding Challenge - Yves CYUZUZO</h3>
        <h2>SpaceX 20 Latest launches</h2>
        {error ? <p>{error.message}</p> : null}
        <div className='container'>
          {!isLoading ?
            <BootstrapTable
              data={ launches }
              striped
              hover
              search
              searchPlaceholder='Search mission name'
            >
              <TableHeaderColumn
                dataField='flight_number'
                isKey
                dataAlign='center'
                dataSort
                searchable={ false }
              >
                Flight number
              </TableHeaderColumn>
              <TableHeaderColumn
                onClick = { e=> this.onSort(e, 'mission_name') }
                dataField='mission_name'

              >
                mission name
              </TableHeaderColumn>
              <TableHeaderColumn
                dataField='rocket_name'
                searchable={ false }
              >
                rocket name
              </TableHeaderColumn>
            </BootstrapTable>
            : <h4>Loading...</h4>
          }
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  launches: state.latestLaunches.launches,
  isLoading: state.latestLaunches.isLoading,
  error: state.latestLaunches.error
})

export default connect(mapStateToProps)(App)

