import React, { Component } from 'react';
import './App.css';
import 'react-bootstrap-table/dist/react-bootstrap-table.min.css';
import {fetchAllLauches} from './modules/action.js';
import { connect } from 'react-redux';
//import 'bootstrap/dist/css/bootstrap.min.css';
import { Table, Pagination, Pager} from 'react-bootstrap';


class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      sortDirection: 'ascending',
      currentPage: 3,
      maxLenghtPerPage: 7,
      currentPageData: [],
      pageNumbers:[]
    };
    props.dispatch(fetchAllLauches());
  }

  onPageData(){
    var allLauches = this.props.launches;
    if(allLauches){
      // Logic for displaying launches
      const indexOfLastTodo = this.state.currentPage * this.state.maxLenghtPerPage;
      const indexOfFirstTodo = indexOfLastTodo - this.state.maxLenghtPerPage;
      var currentLaunches = allLauches.slice(indexOfFirstTodo, indexOfLastTodo);


      var allpages=[];
      for (let i = 1; i <= Math.ceil((allLauches.length)/this.state.maxLenghtPerPage); i++) {
        allpages.push(i);

      }

      this.setState({ 
        currentPageData: currentLaunches,
        pageNumbers: allpages
      });
    }

  }

  handleClick(event, pagenumberr) {
    console.log(pagenumberr)
    this.setState({
      currentPage: pagenumberr
    });
    this.onPageData();
  }

  componentDidUpdate(prevProps, prevState) {
    if(prevProps.launches !== this.props.launches){
      this.onPageData();
    }
  }



  render() {
    const { isLoading, launches, error } = this.props;

    return (
      <div className='App'>
        <h3>Frontend React Coding Challenge - Yves CYUZUZO</h3>
        <h2>SpaceX 20 Latest launches</h2>
        {error ? <p>{error.message}</p> : null}
        <div className='container'>
          {!isLoading ?
            <div>
              <Table striped bordered condensed hover bsClass="table">
                <thead>
                  <tr>
                    <th>Flight number</th>
                    <th>mission name</th>
                    <th >rocket name</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.currentPageData.map(function(account, index) {
                    return (
                      <tr key={ index } data-item={ account }>
                        <td >{account.flight_number}</td>
                        <td >{account.mission_name}</td>
                        <td >{account.rocket_name}</td>
                      </tr>
                    );
                  })}
                </tbody>
              </Table>
              <Pager>
                {this.state.pageNumbers.map(number => {
                  return (
                    <Pagination.Item
                      key={ number }
                      id={ number }
                      onClick={ e => this.handleClick(e, number) }
                      href={ '#/pagenumber/'+number }
                      active= { number === this.state.currentPage }
                    >
                      {number}{' '}
                    </Pagination.Item>
                  );
                })}
              </Pager>
            </div>

            : <h4>Loading...</h4>
          }
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  launches: state.latestLaunches.launches,
  isLoading: state.latestLaunches.isLoading,
  error: state.latestLaunches.error
});

export default connect(mapStateToProps)(App);
