import React, { Component } from 'react';
import './App.css';
import 'react-bootstrap-table/dist/react-bootstrap-table.min.css';
import {fetchAllLauches} from './modules/action.js';
import { connect } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Table, Pager, Pagination } from 'react-bootstrap';


class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      sortDirection: 'ascending',
      currentPage: 1,
      maxLenghtPerPage: 7,
      currentPageDataa: []
    };
    props.dispatch(fetchAllLauches());
    this.handleClick = this.handleClick.bind(this);
    this.onSort = this.onSort.bind(this);
  }

  onSort(event, sortKey, currentPageData){
    var sorted = currentPageData;
    if(this.state.sortDirection ==='descending') {
      sorted.sort((a, b) => b[sortKey].localeCompare(a[sortKey]));
      this.setState({ 
        sortDirection: 'ascending'});
    }else{
      sorted.sort((a, b) => b[sortKey].localeCompare(a[sortKey])).reverse();
      this.setState({ 
        sortDirection: 'descending'});
    }

    //this.setState.currentPageDataa = sorted;
    console.log(sorted)
  }

  componentDidMount() {
    fetchAllLauches();

  }

  handleClick(event) {
    //console.log(this.state.currentPage);
    this.setState({
      currentPage: Number(event.target.id)
    });
  }

  render() {
    const { isLoading, launches, error } = this.props;

    // Logic for displaying page numbers
    const pageNumbers = [];
    var currentPageData;
    if(launches){
      // Logic for displaying launches
      const indexOfLastTodo = this.state.currentPage * this.state.maxLenghtPerPage;
      const indexOfFirstTodo = indexOfLastTodo - this.state.maxLenghtPerPage;
      currentPageData = launches.slice(indexOfFirstTodo, indexOfLastTodo);

      for (let i = 1; i <= Math.ceil((launches.length)/this.state.maxLenghtPerPage); i++) {
        pageNumbers.push(i)

      }
      //console.log(pageNumbers)
    }
    //this.setState({currentPageDataa: currentPageData});
   // console.log(currentPageData)

    const renderPageNumbers = pageNumbers.map(number => {
      return (
        <Pagination.Item
          key={ number }
          id={ number }
          onClick={ this.handleClick }
          href={ '#/pagenumber/'+number }
          active={number === this.state.currentPage}
          >

          {number}

        </Pagination.Item>

      );
    });

    return (
      <div className='App'>
        <h3>Frontend React Coding Challenge - Yves CYUZUZO</h3>
        <h2>SpaceX 20 Latest launches</h2>
        {error ? <p>{error.message}</p> : null}
        <div className='container'>
          {!isLoading ?
            <div>
              <Table striped bordered condensed hover bsClass="table">
                <thead>
                  <tr>
                    <th onClick = { e => this.onSort(e, 'flight_number',currentPageData) }>Flight number</th>
                    <th onClick = { e => this.onSort(e, 'mission_name',currentPageData) }>mission name</th>
                    <th >rocket name</th>
                  </tr>
                </thead>
                <tbody>
                  {currentPageData.map(function(account, index) {
                    return (
                      <tr key={ index } data-item={ account }>
                        <td >{account.flight_number}</td>
                        <td >{account.mission_name}</td>
                        <td >{account.rocket_name}</td>
                      </tr>
                    );
                  })}
                </tbody>
              </Table>
              <Pager>
                {renderPageNumbers}
              </Pager>
            </div>

            : <h4>Loading...</h4>
          }
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  launches: state.latestLaunches.launches,
  isLoading: state.latestLaunches.isLoading,
  error: state.latestLaunches.error
});

export default connect(mapStateToProps)(App);

//export default App;
